let VERSION = '0.0.1' //`${vers.buildMajor}.${vers.buildMinor}.${vers.buildRevision}`
const MASTER = PROGRAM + 'ID_'
const TOKEN = PROGRAM + 'TOKEN_'

const DATE_FORMAT = {
  SQL_DATE: 'YYYY-MM-DD',
  SQL_DATE_TIME: 'YYYY-MM-DD HH:mm:ss',
  DISPLAY: 'DD/MM/YYYY',
  DISPLAY_DATE_TIME: 'DD/MM/YYYY HH:mm:ss',
}

const NUM_FORMAT = {
  D0: '0,0',
  D1: '0,0.0',
  D2: '0,0.00',
  D3: '0,0.000',
  D4: '0,0.0000',
}

const IMAGE_FALLBACK =
  'data:image/png;base64,'

const getPermission = async (menuID) => {
  const params = {
    user_id: Cookies.get(MASTER),
    menu_id: menuID,
  }
  const data = await fetch('post', '/users/permission', params)
  // console.log('perm: ', data)
  if (!data) {
    return {
      open: false,
      view: true,
      add: true,
      edit: true,
      delete: true,
      print: true,
      confirm: true,
    }
  }

  return {
    open: data.action_open,
    view: !data.action_view,
    add: !data.action_add,
    edit: !data.action_edit,
    delete: !data.action_delete,
    print: !data.action_print,
    confirm: !data.action_confirm,
  }
}

const THEME_PROGRAM = '#3c7abb'

export {
  PROGRAM,
  BASE_URL,
  MASTER,
  TOKEN,
  VERSION,
  SOCKET_PATH,
  getPermission,
  DATE_FORMAT,
  NUM_FORMAT,
  IMAGE_URL,
  IMAGE_FALLBACK,
  THEME_PROGRAM,
}
